using Microsoft.Xna.Framework;

namespace PersianCore.Cameras
{
    public struct CameraHistory
    {
        #region Fields & Properties

        public Vector3 Position;
        public Vector3 Target;

        #endregion

        #region Constructor

        public CameraHistory(Vector3 Position, Vector3 Target)
        {
            this.Position = Position;
            this.Target = Target;
        }

        #endregion
    }

    public class CameraHistoryManager
    {
        #region Fields & Properties

        public CameraHistory?[] cameraHistory;

        #endregion

        #region Constructor

        public CameraHistoryManager()
        {
            cameraHistory = new CameraHistory?[9];
        }

        #endregion

        #region Methods

        public void AddToHistory(sbyte index, Vector3 Position, Vector3 Target)
        {
            this.cameraHistory[index] = new CameraHistory(Position, Target);
        }

        public CameraHistory? GetFromHistory(sbyte index)
        {
            return this.cameraHistory[index];
        }

        public void RemoveFromHistory(sbyte index)
        {
            this.cameraHistory[index] = null;
        }

        public void RemoveAllHistory()
        {
            for (sbyte i = 0; i < this.cameraHistory.Length; i++)
            {
                this.cameraHistory[i] = null;
            }
        }

        #endregion
    }
}
