using Microsoft.Xna.Framework.Graphics;
using System.Collections.Generic;

public static class Fonts
{
    #region Fields & Properties

    static Dictionary<string, SpriteFont> fonts = new Dictionary<string, SpriteFont>();

    #endregion

    #region Load

    public static void Load()
    {
        var path = @"Fonts\";
        //if (Persian.RunningEngine)
        //{
        //    path = @"EditorBrowser\" + path;
        //}

        fonts = new Dictionary<string, SpriteFont>();
        var font = Persian.EngineContent.Load<SpriteFont>(path + "Times14");
        fonts.Add("Times14", font);

        font = Persian.EngineContent.Load<SpriteFont>(path + "Title");
        fonts.Add("Title", font);

        font = Persian.EngineContent.Load<SpriteFont>(path + "Regular");
        fonts.Add("Regular", font);
    }

    #endregion

    #region Methods

    public static SpriteFont GetFont(string name)
    {
        SpriteFont font = null;
        if (!fonts.TryGetValue(name, out font))
        {
            Logger.WriteError("Could not load font for LoadingScreen class");
        }
        return font;
    }

    #endregion
}
