using Microsoft.Xna.Framework;

namespace PersianCore.Framework
{
    using PersianCore.Graphics.VertexType;

    public struct Line3D
    {
        public Vector3 Hadi;
        public Vector3 Point;
        internal VertexMesh P0;
        internal VertexMesh P1;

        public static Line3D CreateLin3D(Vector3 p0, Vector3 p1)
        {
            return new Line3D()
            {
                Hadi = p0 - p1,
                Point = p0
            };
        }

        public static Line3D CreateLin3D(VertexMesh p0, VertexMesh p1)
        {
            return new Line3D()
            {
                Hadi = p0.Position - p1.Position,
                Point = p0.Position,
                P0 = p0,
                P1 = p1
            };
        }
    }

}
