using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace PersianCore.Framework.PA
{
    public struct PaAnimation
    {
        public string BID { get; set; }

        /// <summary>
        /// Type of animation 
        /// 1 : Linear
        /// 2 : Bezier
        /// </summary>
        public uint TA { get; set; }

        /// <summary>
        /// Type of transforms
        /// 1 : translation
        /// 2 : Scale
        /// 3 : rotation
        /// </summary>
        public uint TR { get; set; }

        /// <summary>
        /// Dimension
        /// X
        /// Y
        /// Z
        /// </summary>
        public string D { get; set; }

        public float[] Ti { get; set; }

        public float[] Ky { get; set; }

        public float[] ITan { get; set; }

        public float[] OTan { get; set; }

        public Animation ToAnimation()
        {
            return new Animation
            {
                Dimension = D,
                Times = Ti,
                TransformType = TR.ToString(),
                InterpolationType = TA.ToString(),
                InTangent = new BezierInfo { Array = ITan },
                OutTangent = new BezierInfo { Array = OTan },
                Keys = Ky,
                Sid = BID,
                Name = BID,
                Id = BID
            };
        }
    }

}
