using Newtonsoft.Json;
using System.IO;

namespace PersianCore.Framework.PA
{
    public class PaLoader
    {
        public static PaCamera LoadCameraFromFile(string fileName)
        {
            var str = File.ReadAllText(fileName);
            var je = (PaCamera)JsonConvert.DeserializeObject(str, typeof(PaCamera));
            return je;
        }
    }
}