using Microsoft.Xna.Framework;
using PersianCore;
using PersianSettings;

/// <summary>
/// Change the state of screen, this operation will be manage by the ScreenManager
/// </summary>
public enum ScreenState 
{
    //The screen is waiting for running
    NotStarted,
    //The screen is running
    Running,
    //The screen is completed
    Completed
}

public interface IScreen
{
    ScreenState State { get; set; }

    ScreenInfo Info { get; set; }

    bool IsLoaded { get; set; }

    void Reset();

    bool Initialize();

    bool Load(CoreFrameWork CFrameWork);

    void OnPreparingDevice(PreparingDeviceSettingsEventArgs e);

    void Update();

    void Draw();

    void Dispose();
}