using Microsoft.Xna.Framework;

namespace PersianSettings
{
    public struct CubeArea
    {
        #region Fields

        public Vector3 Center;
        public float Height;
        public float Width;
        public float Lenght;

        #endregion

        #region Constructor

        public CubeArea(Vector3 Center, float Hight, float Width, float Lenght)
        {
            this.Center = Center;
            this.Height = Hight;
            this.Width = Width;
            this.Lenght = Lenght;
        }

        #endregion

        #region Methods

        public float Volume()
        {
            return Height * Width * Lenght;
        }

        #endregion
    };

    public class WeatherSet
    {
        /// <summary>
        /// Is Enable or not
        /// </summary>
        public bool Enable { get; set; }

        /// <summary>
        /// Name of the texture likes Rain or Snow, where the path is @"Textures\Environments\{ your texture name }"
        /// </summary>
        public string TextureName = "Rain";
        /// <summary>
        /// is rain or snow
        /// </summary>
        public bool IsRain = true;

        /// <summary>
        /// Type of Weather( Rain,Snow,etc )
        /// </summary>
        public string weatherType = null;

        /// <summary>
        /// Number of particles in region
        /// </summary>
        public uint NumberOfParticles = 0;

        /// <summary>
        /// Active depending
        /// </summary>
        public bool ActiveDepending;

        /// <summary>
        /// if depending was active, so set depends on who 
        /// </summary>
        public string DependsOnWho;

        /// <summary>
        /// Initial progress
        /// </summary>
        public float InitialProgress = 1;
        /// <summary>
        /// Region that weather is appling it's behavioral 
        /// </summary>
        public CubeArea cubeArea = new CubeArea();

        /// <summary>
        /// Turbulence  used for snow
        /// </summary>
        public float Turbulence = 0.2f;

        /// <summary>
        /// Enable fade in animation for weather
        /// </summary>
        public bool FadeIn = true;

        /// <summary>
        /// Progress step for fade in or fade out
        /// </summary>
        public float FadeProgressStep = 0.01f;

        /// <summary>
        /// Progress time for each step in milliseconds
        /// </summary>
        public int FadeProgressTime = 3000;

        /// <summary>
        /// From velocity
        /// </summary>
        public Vector3 FromVelocity = new Vector3(0, -20, 0);

        /// <summary>
        /// To this velocity
        /// </summary>
        public Vector3 ToVelocity = new Vector3(0, -20, 0);

        /// <summary>
        /// The speed for the velocity
        /// </summary>
        public float VelocityStep = 0.1f;
    }
}