using System;

namespace Pipelines
{
    enum MapType
    {
        Normal,
        Specular,
        Emissive
    };

    public static class EnumsConverter
    {
        public static T StringToEnum<T>(string name)
        {
            if (Enum.IsDefined(typeof(T), name))
            {
                return (T)Enum.Parse(typeof(T), name);
            }
            return default(T);
        }
    }
}
