using System;

namespace PersianEditor.Windows
{
    public partial class ExplorerWin : BaseWin
    {
        #region Fields & Properties

        public event EventHandler OnSelectingObject;

        #endregion

        #region Constructor

        public ExplorerWin()
            : this(null)
        {
        }

        public ExplorerWin(PersianCore.CoreFrameWork coreFrameWork)
        {
            InitializeComponent();
            this.Ctrl.coreFrameWork = coreFrameWork;
            this.Ctrl.OnSelectingObject += delegate(object sender, EventArgs e)
            {
                if (OnSelectingObject != null)
                {
                    OnSelectingObject(sender, e);
                }
            };
        }

        #endregion

        #region Events

        protected override void Base_Loaded(object sender, System.Windows.RoutedEventArgs e)
        {
            base.Base_Loaded(sender, e);
        }

        protected override void OnClosing(System.ComponentModel.CancelEventArgs e)
        {
            this.Ctrl.coreFrameWork = null;
            base.OnClosing(e);
        }

        #endregion
    }
}
